mapper.select:
   		SELECT ID, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_STOCK
		FROM TBL_PRODUCT 
		WHERE ID = #{id}

mapper.update:

   		UPDATE TBL_PRODUCT 							
		SET 									
			PRODUCT_NAME = #{productName}, 
			PRODUCT_PRICE = #{productPrice}, 
			PRODUCT_STOCK = #{productStock}
		WHERE ID = #{id}
